import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const UserProfile = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const userId = localStorage.getItem('userId');

    if (!userId) {
      setError('User not logged in');
      return;
    }

    axios.get(`http://localhost:5050/api/users/profile/${userId}`)
      .then(res => {
        setProfile(res.data);
        console.log('Profile response:', res.data);
      })
      .catch(err => {
        console.error(err);
        setError('Failed to load profile');
      });
  }, []);

  const handleEdit = () => {
    navigate('/edit-profile');
  };

  if (error) return <div style={{ color: 'red', textAlign: 'center', marginTop: '2rem' }}>{error}</div>;
  if (!profile) return <div style={{ textAlign: 'center', marginTop: '2rem' }}>Loading profile...</div>;

  return (
    <div style={{ padding: '30px', maxWidth: '600px', margin: '0 auto', fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '30px', color: '#333' }}>Your Profile</h1>

      <div style={{
        background: '#fff',
        border: '1px solid #ddd',
        borderRadius: '10px',
        padding: '25px',
        boxShadow: '0 2px 8px rgba(175, 64, 12, 0.05)'
      }}>
        <div style={{ marginBottom: '15px' }}>
          <label style={{ fontWeight: 'bold' }}>First Name:</label>
          <div style={{ marginTop: '5px', color: '#555' }}>{profile.FirstName}</div>
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label style={{ fontWeight: 'bold' }}>Last Name:</label>
          <div style={{ marginTop: '5px', color: '#555' }}>{profile.LastName}</div>
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label style={{ fontWeight: 'bold' }}>Email:</label>
          <div style={{ marginTop: '5px', color: '#555' }}>{profile.Email}</div>
        </div>

        <div style={{ marginBottom: '25px' }}>
          <label style={{ fontWeight: 'bold' }}>Phone Number:</label>
          <div style={{ marginTop: '5px', color: '#555' }}>{profile.PhoneNumber}</div>
        </div>

        <div style={{ textAlign: 'center' }}>
          <button
            onClick={handleEdit}
            style={{
              backgroundColor: '#007BFF',
              color: '#fff',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              fontSize: '16px',
              cursor: 'pointer'
            }}
          >
            Edit Profile
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
